class TrieNode
  attr_accessor :children, :is_word
  def initialize
    @children = {}
    @is_word = false
  end

  def insert(word)
    idx, node = 0, self
    while idx < word.length
      node.children[word[idx]] ||= TrieNode.new
      node = node.children[word[idx]]
      idx += 1
    end
    node.is_word = true
  end
end

# @param {Character[][]} board
# @param {String[]} words
# @return {String[]}
def find_words(board, words)
  res = Set.new
  trie = TrieNode.new
  words.each do |word|
    trie.insert(word)
  end
  0.upto(board.length - 1) do |row|
    0.upto(board[row].length - 1) do |col|
      if trie.children[board[row][col]]
        dfs(board, trie, row, col, res)
      end
    end
  end
  res.to_a
end

def dfs(board, trie, row, col, res, path = [], visited = Hash.new{|h,k| h[k] = {}})
  return res if row < 0 || row >= board.length || col < 0 || col >= board[row].length || visited[row][col] || !trie.children[board[row][col]]
  path.push(board[row][col])
  trie = trie.children[board[row][col]]
  visited[row][col] = true
  res.add(path.join) if trie.is_word
  dfs(board, trie, row, col + 1, res, path, visited)
  dfs(board, trie, row, col - 1, res, path, visited)
  dfs(board, trie, row + 1, col, res, path, visited)
  dfs(board, trie, row - 1, col, res, path, visited)
  visited[row][col] = false
  path.pop
  res
end

